import React from "react";
import styled from "styled-components";

const Container = styled.div`
  display: flex;
  flex-direction: column;
  flex: 1.6;
  height: 100%;
  width: 100%;
  border-right: 1px solid #dadada;
`;

const ProfileInfoDiv = styled.div`
  display: flex;
  flex-direction: row;
  background: #ededed;
  padding: 10px;
`;

const ProfileImage = styled.img`
  width: 32px;
  height: 32px;
  border-radius: 50%;
`;

const SearchBox = styled.div`
  display: flex;
  flex-direction: row;
  background: #f6f6f6;
  padding: 10px;
`;

export const SearchContainer = styled.div`
  display: flex;
  flex-direction: row;
  background: white;
  border-radius: 16px;
  width: 100%;
  padding: 5px 10px;
  gap: 10px;
`;

const SearchIcon = styled.img`
  width: 28px;
  height: 28px;
`;

export const SearchInput = styled.input`
  width: 100%;
  outline: none;
  border: none;
  font-size: 15px;
`;

const ContactComponent = (props) => {
  const { userData, setChat, userInfo } = props;
  const [searchResult, setSearchResult] = useState();

  const otherUser =
    userData.channelUsers?.find(
      (userObj) => userObj.email !== userInfo.email
    ) || userData;

  const lastMessage =
    userData.messages && userData.messages.length
      ? userData.messages[userData.messages.length - 1]
      : {};

  return (
    <ContactItem onClick={() => setChat({ channelData: userData, otherUser })}>
      <ProfileIcon src={otherUser?.profilePic} />
      <ContactInfo>
        <ContactName>{otherUser?.name}</ContactName>
        <MessageText>{lastMessage?.text}</MessageText>
      </ContactInfo>
      <MessageTime>
        {" "}
        {lastMessage && new Date(lastMessage?.addedOn).getUTCDate()}
      </MessageTime>
    </ContactItem>
  );
};

function ContactListComponent() {
  return (
    <Container>
      <ProfileInfoDiv>
        <ProfileImage src="/profile/theindiandev.jpeg" />
      </ProfileInfoDiv>
      <SearchBox>
        <SearchContainer>
          <SearchIcon src="/search-icon.svg" />
          <SearchInput placeholder="Search or start a new chat" />
        </SearchContainer>
      </SearchBox>
    </Container>
  )
}

export default ContactListComponent
